import React from "react";
import { reducerFunction } from "../../helpers";

const initiialState = {
  mount: false,
  count: 0,
  a: 10,
};

export const Body = function () {
  // const [b, setB] = React.useState(20);
  const [state, setState] = React.useReducer(reducerFunction, initiialState);

  React.useEffect(function () {
    setTimeout(() => {
      setState({ mount: true });
    }, 2000);
  }, []);

  // const onBtnClick = () => {
  //   // setState({
  //   //   mount: !state.mount,
  //   //   count: state.count + 1,
  //   // });
  //   setB(b * 2);
  // };
  return !state.mount ? (
    <img
      style={{
        width: "300px",
        height: "100px",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
      src="https://miro.medium.com/v2/resize:fit:1400/1*CsJ05WEGfunYMLGfsT2sXA.gif"
      alt="img/gif"
    ></img>
  ) : (
    <div className="body-container">
      <p>{`mount -${state.mount} - A=${state.a}`}</p>
      <p>{`Count Click Me - ${state.count}`}</p>
      {/* <button onClick={onBtnClick}>Click Me</button> */}
    </div>
  );
};
