import React from "react";
import { reducerFunction } from "../../helpers";

const initialState = {
  mount: false,
};

export const Body = function (props) {
  const [b,setB] = React.useState()
  const [state, setState] = React.useReducer(reducerFunction, initialState);

  React.useEffect(
    function () {
      // get The Date from Api
      setTimeout(() => {
        setState({
          mount: true,
        });
      }, 2000);
    },
    [state] //dependeny list
  );

  const clickHandler = function () {
    setState({
      mount: !state.mount,
    });
  };

  return !state.mount ? (
    <div>Loading...</div>
  ) : (
    <div className="body-container">
      {`mount- ${state.mount}`}
      <button onClick={clickHandler}>Click Me</button>
    </div>
  );
};
