import React from "react";
import { reducerFunction } from "../../helpers";

const initiialState = {
  mount: false,
  count: 0,
  a: 10,
};

export const Body = function () {
  const [b, setB] = React.useState(2);
  const [state, setState] = React.useReducer(reducerFunction, initiialState);

  React.useEffect(function () {
      setState({ mount: true });
  }, []);

  const onBtnClick = () => {
    // setState({
    //   mount: !state.mount,
    //   count: state.count + 1,
    // });
    setB(b * 2);
  };
  return !state.mount ? (
    <img
      style={{
        width: "300px",
        height: "100px",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
      src="https://usagif.com/wp-content/uploads/loading-1.gif.webp"
      alt="img/gif"
    ></img>
  ) : (
    <div className="body-container">
      <p>{`mount -${state.mount} - A=${state.a} - B = ${b} `}</p>
      <p>{`Count Click Me - ${state.count}`}</p>
      <button onClick={onBtnClick}>Update B</button>
    </div>
  );
};
